document.getElementById("mobile-menu-button")?.addEventListener("click",()=>{const e=document.getElementById("mobile-menu");e&&(e.classList.contains("hidden")?(e.classList.remove("hidden"),setTimeout(()=>{e.classList.add("max-h-64","opacity-100"),e.classList.remove("max-h-0","opacity-0")},10)):(e.classList.add("max-h-0","opacity-0"),e.classList.remove("max-h-64","opacity-100"),setTimeout(()=>{e.classList.add("hidden")},300)))});
